const formPopup=document.querySelector(".form-wrapper"),dateFrom=formPopup.querySelector(".arrival-date-input"),dateTo=formPopup.querySelector(".departure-date-input"),adults=formPopup.querySelector(".adults-count-input"),kids=formPopup.querySelector(".kids-count-input");let times=0;function showHide(){++times%2!=0?(formPopup.classList.remove("form-down"),formPopup.classList.add("form-up")):(formPopup.classList.remove("form-up"),formPopup.classList.add("form-down"))}window.onload=function(){adults.value=localStorage.getItem("adults"),kids.value=localStorage.getItem("kids")},formPopup.addEventListener("submit",function(e){dateFrom.value&&dateTo.value&&adults.value&&kids.value?(localStorage.setItem("adults",adults.value),localStorage.setItem("kids",kids.value)):(e.preventDefault(),alert("Нужно заполнить все поля!"))});